    //             position: "top-center"
    //         });
    //     } else if (password.length < 6) {
    //         toast.error("password must be 6 char!", {
    //             position: "top-center"
    //         });
    //     } else {
    //         // console.log("user login succesfully done");


    //         const data = await fetch("/login",{
    //             method:"POST",
    //             headers:{
