import React, { useContext } from 'react'
import Avatar from '@mui/material/Avatar';
import "./header.css"
import { LoginContext } from './ContextProvider/Context';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { useNavigate , NavLink } from "react-router-dom"

const Header = () => {

  const { logindata, setLoginData } = useContext(LoginContext);
  //console.log(logindata);

  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);
  const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const history = useNavigate();

  return (
    <>
        <header>
            <nav>
                <h1>Personal Trainer Web</h1>
                <div className='avatar'>
                  {
                    logindata.ValidUserOne ? <Avatar style={{background: "salmon", fontWeight:"bold", textTransform:"capitalize"}}>{logindata.ValidUserOne.fname[0].toUpperCase()}</Avatar>:
                    <Avatar style={{background: "blue"}} onClick={handleClick}></Avatar>
                  }
                </div>

                <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          'aria-labelledby': 'basic-button',
        }}
      >
        <MenuItem onClick={handleClose}>Profile</MenuItem>
        <MenuItem onClick={handleClose}>My account</MenuItem>
        <MenuItem onClick={handleClose}>Logout</MenuItem>
      </Menu>
            </nav>
        </header>
    </>
  )
}

export default Header
