import React, { useContext, useEffect ,useState} from 'react'
import { useNavigate } from 'react-router-dom';
import { LoginContext } from './ContextProvider/Context';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';

const Adminboard = () =>{


const [data, setData] = useState(false);
const { logindata, setLoginData } = useContext(LoginContext);
const history = useNavigate();

const AdminboardValid = async () => {
    let token = sessionStorage.getItem("usersdatatoken");

    const res = await fetch("/validadmin", {
        method: "GET",
        headers: {
            "Content-Type": "application/json",
            "Authorization": token
        }
    });
      //가져온 response data를 json화 하여 data에 저장합니다.
      const data = await res.json();
      //console.log(data);

      if(data.status === 401 || !data){
        // => sessionStorage의 usersdatatoken을 가져와 확인하기 떄문에 만약 그것을 지우면 에러가 됨. 
        history("*");
      }else{
        //즉 이곳은 user 가 웹사이트에 로그인 되어있고 뿐만아니라 verify 됬다는것을 의미 하여 setLoginData를 사용하여 현재의 logindata <- data를 집어넣어 줍니다.
      //console.log("user verify");
            setLoginData(data)
            history("/admin");
      }
  }

  useEffect(() => {
    setTimeout(() => {
        AdminboardValid();
        setData(true)
    }, 2000)
}, [])
}

export default Adminboard
