import Reac, { useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Button from 'react-bootstrap/Button';
import { height } from '@mui/system';


const ContactUs = () => {

  const [inpval, setInpval] = useState({
    fname: "",
    email: "",
    phone: "",
    content: ""
});

  const setval =  (e) => {
    e.preventDefault();
    console.log(e.target.value);
    const { name, value } = e.target;

        setInpval(() => {
            return {
                ...inpval,
                [name]: value
            }
        })




  }
 

  return (
    <>
        <section>
                <div className="form_data">
                    <div className="form_heading">
                        <h1>Contact Us</h1>
                    </div>

                    <form>
                        <div className="form_input">
                            <label htmlFor="fname">Name</label>
                            <input type="text" onChange="" value={inpval.fname} name="fname" id="fname" placeholder='Enter Your Name' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="email">Email</label>
                            <input type="email" onChange="" value={inpval.email} name="email" id="email" placeholder='Enter Your Email Address' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="phone">Phone Number</label>
                            <input type="text" onChange="" value={inpval.phone} name="phone" id="phone" placeholder='Enter Your Phone Number' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="content">Message</label>
                        </div>
                        <div>
                            <textarea style={{width: "100%", height: "20vh"}} value={inpval.content} name="content" id="content" placeholder='Enter Your Question'></textarea>
                        </div>
                        <Button variant="primary" type="submit">Submit</Button>
                    </form>
                    <ToastContainer />
                </div>
            </section>
    </>
  )
}

export default ContactUs
