import Reac, { useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Button from 'react-bootstrap/Button';
import { height } from '@mui/system';


const ContactUs = () => {

  const [inpval, setInpval] = useState({
    fname: "",
    email: "",
    phone: "",
    content: ""
});

  const setval =  (e) => {
    e.preventDefault();
    console.log(e.target.value);
    const { name, value } = e.target;

        setInpval(() => {
            return {
                ...inpval,
                [name]: value
            }
        })
  };

  const sendData = async (e) => {
    e.preventDefault();

    const {  fname, email, phone, content  } = inpval;

    if(fname === ""){
      toast.error("Name is required!", {
        position: "top-center"
    });
    }else if (email === "") {
      toast.error("Email is required!", {
          position: "top-center"
      });
  } else if (!email.includes("@")) {
      toast.warning("includes @ in your email!", {
          position: "top-center"
      });
  } else if (phone === "") {
      toast.error("Phone number is required!", {
          position: "top-center"
      });
  } else if (content === "") {
      toast.error("Please type your question", {
          position: "top-center"
      });
  } else {
       //console.log("successfully done");
  }

  const data = await fetch("/contact",{
    method:"POST",
    headers:{
        "Content-Type":"application/json"
    },
    body:JSON.stringify({
      fname, email, phone, content 
    })
});

const res = await data.json();
  //console.log(res);

};


  return (
    <>
        <section>
                <div className="form_data">
                    <div className="form_heading">
                        <h1>Contact Us</h1>
                    </div>

                    <form>
                        <div className="form_input">
                            <label htmlFor="fname">Name</label>
                            <input type="text" onChange={setval} value={inpval.fname} name="fname" id="fname" placeholder='Enter Your Name' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="email">Email</label>
                            <input type="email" onChange={setval} value={inpval.email} name="email" id="email" placeholder='Enter Your Email Address' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="phone">Phone Number</label>
                            <input type="text" onChange={setval} value={inpval.phone} name="phone" id="phone" placeholder='Enter Your Phone Number' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="content">Message</label>
                        </div>
                        <div>
                            <textarea style={{width: "100%", height: "20vh"}} value={inpval.content} onChange={setval} name="content" id="content" placeholder='Enter Your Question'></textarea>
                        </div>
                        <Button onClick={sendData} variant="primary" type="submit">Submit</Button>
                    </form>
                    <ToastContainer />
                </div>
            </section>
    </>
  )
}


export default ContactUs
