import { Fragment, useEffect } from 'react';
import Header from './components/Header';
import Login from './components/Login';
import {Routes, Route} from 'react-router-dom';
import Register from './components/Resister';
import Home from './components/Home';
import Dashboard from './components/Dashboard';
import Error from './components/Error';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';




function App() {

  const DashboardValid = async () => {
    let token = localStorage.getItem("usersdatatoken");

    const res = await fetch("/validuser", {
        method: "GET",
        headers: {
            "Content-Type": "application/json",
            "Authorization": token
        }
    });

      //가져온 response data를 json화 하여 data에 저장합니다.
      const data = await res.json();
      //console.log(data);

      if(data.status === 401 || !data){
        console.log("error page redirect")// => localStorage의 usersdatatoken을 가져와 확인하기 떄문에 만약 그것을 지우면 에러가 됨. 
        history("*");
      }else{
        //즉 이곳은 user 가 웹사이트에 로그인 되어있고 뿐만아니라 verify 됬다는것을 의미 하여 setLoginData를 사용하여 현재의 logindata <- data를 집어넣어 줍니다.
        console.log("user verify");
            setLoginData(data)
            history("/dash");
      }
  }

  useEffect(() => {
    DashboardValid();
  }, [])

  return (
    <Fragment>
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route path="/dash" element={<Dashboard />} />
        <Route path="*" element={<Error />} />
      </Routes>
    </Fragment>
  );
}

export default App;
