import React, { useContext, useEffect } from 'react'
import {useNavigate} from 'react-router-dom';
import { LoginContext } from './ContextProvider/Context';

const Dashboard = () => {

  const {logindata, setLoginData} =  useContext(LoginContext);
  console.log(logindata);
   //useNavigate는 양식이 제출되거나 특정 event가 발생할 때,  url을 조작할 수 있는 interface를 제공합니다.
  const history = useNavigate();

  const DashboardValid = async() => {
      let token = localStorage.getItem('usersdatatoken');
      //console.log(token);
      const res = await fetch("/validuser", {
        method: 'GET',
        headers: {
          "Content-Type": "application/json",
          "Authorization": token
        }
      });

      const data = await res.json();
      //console.log(data);

      if(data.status === 401 || !data){
        console.log("error page redirect")// => localStorage의 usersdatatoken을 가져와 확인하기 떄문에 만약 그것을 지우면 에러가 됨. 
        history("*");
      }else{
        console.log("user verified")
        
        history("/dash");
      }
  }

  useEffect(() => {
    DashboardValid();
  }, [])

  return (
    <>
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
      <img src="./man.png" style={{ width: "200px", marginTop: 20 }} alt="" />
      <h1>User Email: ryangu8812@gmail.com</h1>
     </div>
    </>
  )
}

export default Dashboard
